import { Component } from '@angular/core';
import { Book, BookService } from 'src/app/services/book.service';

@Component({
  selector: 'app-add-book',
  templateUrl: './add-book.component.html',
  styleUrls: ['./add-book.component.css']
})
export class AddBookComponent {
  book: Book = { title: '', author: '', price: 0 };

  constructor(private bookService: BookService) {}

  onSubmit() {
    this.bookService.addBook(this.book).subscribe(() => {
      alert('Book added successfully!');
      this.book = { title: '', author: '', price: 0 };
    });
  }

}
<mat-card>
  <h2>Add Book</h2>
  <form (ngSubmit)="onSubmit()" #bookForm="ngForm">
    <mat-form-field appearance="fill">
      <mat-label>Title</mat-label>
      <input matInput [(ngModel)]="book.title" name="title" required>
    </mat-form-field>

    <mat-form-field appearance="fill">
      <mat-label>Author</mat-label>
      <input matInput [(ngModel)]="book.author" name="author" required>
    </mat-form-field>

    <mat-form-field appearance="fill">
      <mat-label>Price</mat-label>
      <input matInput type="number" [(ngModel)]="book.price" name="price" required>
    </mat-form-field>

    <button mat-raised-button color="primary" type="submit">Add Book</button>
  </form>
</mat-card>
